import React, { useState} from 'react';
import QuoteForm from '../components/QuoteComponents/QuoteForm';
import { Element } from 'react-scroll';


const QuoteContainer = () => {

  const [selectedFile, setSelectedFile] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);

  if (selectedFile && selectedFile.type.startsWith('image')) {
    setImagePreviewUrl(URL.createObjectURL(selectedFile));
  }

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('file', selectedFile);
    console.log('Form submitted', formData);

    console.log('Form submitted', formData);

    if (selectedFile && selectedFile.type.startsWith('image')) {
      console.log('File is an image');
    } else {
      console.log('File is not an image');
    }
  }

  return (
    <Element name='quote'>
      <QuoteForm onSubmit={handleSubmit} onFileChange={handleFileChange} />
      {imagePreviewUrl && <img src={imagePreviewUrl } alt='Preview' style={{width: '100px', height: '100px'}} />}
    </Element>
  );
}

export default QuoteContainer;
